  constructor(private db: Firestore, private servicesAuth: ServicesAuth, private servicesPages: ServicesService, private infoService: InfoService, private auth: Auth ) {}

  setLevel(event: any) {
    this.fnChangeNamesAndLinks(parseInt(event.target.parentElement.name));
    console.log(this.selectedLevel, this.level);
  }

  fnHiddeLevels(level: number | undefined) {
    switch (level) {
      case 1:
        this.showc2 = true;
        this.showc3 = true;
        this.showc4 = true;
        this.showc5 = true;
        break;
      case 2:
        this.showc3 = true;
        this.showc4 = true;
        this.showc5 = true;
        break;
      case 3:
        this.showc4 = true;
        this.showc5 = true;
        break;
      case 4:
        this.showc5 = true;
        break;
      default:
    }
  }

